    console.log(props)
    return (
        <div className={props.className}>
            <label>{props.label}</label>
            <textarea 
            name={props.id} 
            id={props.id} 
            value={value} 
            onChange={props.onChange}/>
        </div>
    );
}

export default TexteArea
