import React, { useState, useEffect } from "react";
import FieldInput from "../FieldInput/FieldInput";
import TexteArea from "../TextArea/TexteArea";
import Select from "../Select/Select";
import Axios from "axios";
import "./Formulaire.css";
import Logo from "../Images/logo.png";
import { useParams } from "react-router-dom";

const CheminCreation = (props) => {

  const [chemin, setChemin] = useState({
    expertActif: "",
    client: "",
    titre: "",
    status: "actif"
  });
  const [liste_clients, setListe_clients] = React.useState([]);

  let clients;

React.useEffect(() =>
    {
    Axios
        .get("https://127.0.0.1:8000/api/clients")
        .then(response => response.data["hydra:member"])
        .then(clients => 
          { 
              let liste_cli=[];
              clients.forEach((item) =>{
                  liste_cli.push([item.id, item.prenom+" "+item.nom]);
              })
              setListe_clients(liste_cli);
              console.log(liste_cli);
          })


    },[])

  const handleChange = ({ currentTarget }) => {
    const value = currentTarget.value;
    const name = currentTarget.name;

    setChemin({ ...chemin, [name]: value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    console.log(chemin);
    try {
      const response = await Axios.post(
        "https://127.0.0.1:8000/api/chemins",chemin)
        .then((response) => {

        });
    } catch (error) {
        console.log(error.response);}
    };

  return (
    <>
      <div>
        <div className="row headerContainer">
          <img
            id="logo_foxt"
            className="fieldInput col-lg-8"
            src={Logo}
            alt="mon image"
          />
          <div className="title fieldInput col-lg-8">NOUVEAU CHEMIN</div>
        </div>
        <form
          id="formulaire"
          className="container-fluid form"
          onSubmit={handleSubmit}
        >
          <div>
            <div className="row">
            <Select
                name="status"
                id="Status"
                className="select col-lg-4"
                label="Status"
                type="select"
                options={[
                  ["actif", "Actif"],
                  ["inactif", "Inactif"],
                ]}
                value={chemin.status}
                onChange={handleChange}
              />
              <Select
                id="client"
                className="select col-lg-4"
                name="client"
                label="Client"
                type="select"
                selected=""
                options={liste_clients}
                value={"https://127.0.0.1:8000/api/users/"+chemin.client}
                onChange={handleChange}
              />
              <FieldInput
                id="titre"
                className="fieldInput col-lg-4"
                label="Titre"
                type="text"
                name="titre"
                value={""}
                onChange={handleChange}
              />

            </div>
          </div>
          <input
            className="btn btn-secondary btn-lg"
            type="submit"
            value="Envoyer"
          />
        </form>
      </div>
    </>
  );
};

export default CheminCreation;
