import React, { useState } from 'react';
import { v4 } from 'uuid';
import'./Select.css'

const Select = ({className, selected, id, name, label, value, onChange}) => {

    return (
        <div className={className}>
            <label>{label}</label>
            <select 
                id={id} 
                key={v4()} 
                defaultValue={selected} 
                name={name}

                onChange={onChange}
                >
                {
                options.map((choix) =>
                    <option key={v4()} value={choix[0]}>{choix[1]}</option>
                )}
           </select>
        </div>
    );
 }

export default Select;
