import React from 'react'
import FullCalendar, { formatDate } from '@fullcalendar/react'
import dayGridPlugin from '@fullcalendar/daygrid'
import timeGridPlugin from '@fullcalendar/timegrid'
import interactionPlugin from '@fullcalendar/interaction'
import { createEventId } from './event-utils'

export default class Calendrier extends React.Component {

  state = {
    weekendsVisible: false,
    currentEvents: []
  }

  INITIAL_EVENTS = [] // utile pour interface clients


  handleWeekendsToggle = () => {
    this.setState({
      weekendsVisible: !this.state.weekendsVisible
    })
  }

  handleDateSelect = (selectInfo) => {
    let title = "Disponible"
    let calendarApi = selectInfo.view.calendar

    calendarApi.unselect() 

    calendarApi.addEvent({
    id: createEventId(),
    title,
    start: selectInfo.startStr,
    end: selectInfo.endStr,
    allDay: selectInfo.allDay
    })

  }

  handleEventClick = (clickInfo) => {
    clickInfo.event.remove()
  }

  handleEvents = (events) => {
    this.setState({
      currentEvents: events
    })
  }


  renderEventContent = (eventInfo) => {
    return (
      <>
        <b>{eventInfo.timeText}</b>
        <i>{eventInfo.event.title}</i>
      </>
    )
  }
  render() {
    return (
      <div className='calendrier'>
        <div className='calendrier-main'>
          <button className='btn btn-' onClick={this.handleWeekendsToggle}>{this.state.weekendsVisible ? "Masquer" : "Afficher"} les week-end</button>
          <br /><hr /><br />
          <FullCalendar
            plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
            headerToolbar={{
              left: 'prev,next today',
              center: 'title',
              right: 'dayGridMonth,timeGridWeek,timeGridDay'
            }}
            height = {650}
            initialView='timeGridWeek'
            allDaySlot = {false}
            editable={true}
            selectable={true}
            selectMirror={true}
            dayMaxEvents={true}
            weekends={this.state.weekendsVisible}
            initialEvents={this.INITIAL_EVENTS} // pour tester des rdv de base (utile sur interface clients)
            select={this.handleDateSelect}
            eventContent={this.renderEventContent}
            eventClick={this.handleEventClick}
            eventsSet={this.handleEvents}
          />
        </div>
      </div>
    )
  }

}
