import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react'
import dayGridPlugin from '@fullcalendar/daygrid'
import timeGridPlugin from '@fullcalendar/timegrid'
import interactionPlugin from '@fullcalendar/interaction'
import './Calendriers.css';

function Calendriers() {

  const [weekEnd, setweekEnd] = useState(true);
  const [boutonWeekEnd, setboutonWeekEnd] = useState("Masquer le weekend");
  const [Dispo, setDispo] = useState([]);
  const [eventGuid, setEventGuid] = useState(0);

  const handleWeekend = () =>{
    if (weekEnd) {
      setboutonWeekEnd("Afficher le weekend")
    }
    else{
      setboutonWeekEnd("Masquer le weekend")
    }
    setweekEnd(!weekEnd);
  }

  function createEventId() {
    return String(eventGuid++)
  }

  function handleDateSelect (selectInfo) {
    let title = prompt('Please enter a new title for your event')
    let calendarApi = selectInfo.view.calendar

    calendarApi.unselect() // clear date selection

    if (title) {
      calendarApi.addEvent({
        id: createEventId(),
        title,
        start: selectInfo.startStr,
        end: selectInfo.endStr,
        allDay: selectInfo.allDay
      })
    }
  }

  function handleEventClick (clickInfo) {
    if (confirm(`Are you sure you want to delete the event '${clickInfo.event.title}'`)) {
      clickInfo.event.remove()
    }
  }

  function handleEvents (events)  {
    this.setState({
      currentEvents: events
    })
  }

  function renderEventContent(eventInfo) {
    return (
      <>
        <b>{eventInfo.timeText}</b>
        <i>{eventInfo.event.title}</i>
      </>
    )
  }




  return (
    <div className="container">        
        <button onClick={handleWeekend} className="btn col-md-3 btn-info">{boutonWeekEnd}</button>
        <div className="card" >
          <FullCalendar
            plugins={[ dayGridPlugin, timeGridPlugin, interactionPlugin]}
            initialView="timeGridWeek"
            weekends={weekEnd}
            dateClick = {handleDateClick}
            editable = {true}
            event = {Dispo}
            eventClick={handleEventClick}
            select={handleDateSelect}
          />
        </div>
    </div>
  );
}


export default Calendriers;
