import React, { useState, useEffect } from "react";
import FieldInput from "../FieldInput/FieldInput";
import TexteArea from "../TextArea/TexteArea";
import Select from "../Select/Select";
import Axios from "axios";
import NavCustomerSuccess from "../CustomersSuccess/NavCustomerSuccess";
import Logo from "../Images/logo.png";
import { useParams } from "react-router-dom";

const ExpertCreation = (props) => {
  const [user, setUser] = useState({
    email: "",
    password: "",
    roles: ["EXPERT"],
    nom: "",
    prenom: "",
  });

  const [expert, setExpert] = useState({
    email: "",
    password: "",
    role: ["EXPERT"],
    civilite: "M.",
    nom: "",
    prenom: "",
    telephone: "",
    adresse: "",
    codePostal: "",
    ville: "",
    status: "actif",
    dateCreation: new Date(),
    presentation: "",
    user: "",
  });

  const [errors, setErrors] = useState({
    email: "",
    password: "",
    civilite: "",
    nom: "Le nom est obligatoire ",
    prenom: "",
    telephone: "",
    adresse: "",
    codePostal: "",
    ville: "",
    status: "actif",
    date_creation: "",
    presentation: "",
  });

  const handleChange = ({ currentTarget }) => {
    const value = currentTarget.value;
    const name = currentTarget.name;

    setUser({ ...user, [name]: value });
    setExpert({ ...expert, [name]: value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    let idUser;
    let url = "";
    console.log(user);

    try {
      const response = await Axios.post(
        "https://127.0.0.1:8000/api/users",
        user
      ).then((response) => {
          idUser = response.data.id;
          url = "https://127.0.0.1:8000/api/users/"+idUser;
          callbackSetExpert(url);
        });
    } catch (error) {
        console.log(error.response);}
    };

    const callbackSetExpert = async (url) => {
      let newExpert = {
        ...expert,
        user: url
      };
      console.log(newExpert)
      Axios.post("https://127.0.0.1:8000/api/experts", newExpert);
  };


  return (
    <>
      <div>
      <NavCustomerSuccess id={localStorage.id} />
        <div>
            <h2>CREATION EXPERT FOXT</h2>
        </div>
        </div>
        <form
          id="formulaire"
          className="container-fluid form"
          onSubmit={handleSubmit}
        >
          <div>
            <div className="row">
              <Select
                id="titre"
                className="select col-lg-4"
                name="civilite"
                label="Civilité"
                type="select"
                selected="M."
                options={[
                  ["M.", "Monsieur"],
                  ["Mme", "Madame"],
                  ["Melle", "Mademoiselle"],
                ]}
                value={expert.civilite}
                onChange={handleChange}
                error={errors.civilite}
              />
              <FieldInput
                id="prenom"
                className="fieldInput col-lg-4"
                label="Prénom"
                type="text"
                name="prenom"
                value={expert.prenom}
                onChange={handleChange}
                error={errors.prenom}
              />
              <FieldInput
                id="nom"
                className="fieldInput col-lg-4"
                label="Nom"
                type="text"
                name="nom"
                value={expert.nom}
                onChange={handleChange}
                error={errors.nom}
              />
            </div>
            <div className="row">
              <FieldInput
                name="adresse"
                id="adresse"
                className="fieldInput col-lg-4"
                label="Adresse"
                type="text"
                value={expert.adresse}
                onChange={handleChange}
                error={errors.adresse}
              />
              <FieldInput
                name="codePostal"
                id="codePostal"
                className="fieldInput col-lg-4"
                label="Code postal"
                type="text"
                value={expert.codePostal}
                onChange={handleChange}
                error={errors.codePostal}
              />
              <FieldInput
                name="ville"
                id="ville"
                className="fieldInput col-lg-4"
                label="Localité"
                type="text"
                value={expert.ville}
                onChange={handleChange}
                error={errors.ville}
              />
            </div>
            <div className="row">
              <FieldInput
                name="telephone"
                id="telephone"
                className="fieldInput col-lg-6"
                label="Téléphone"
                type="text"
                value={expert.telephone}
                onChange={handleChange}
                error={errors.telephone}
              />
              <FieldInput
                name="email"
                id="email"
                className="fieldInput col-lg-6"
                label="Email"
                type="email"
                value={expert.email}
                onChange={handleChange}
                error={errors.email}
              />
            </div>
            <div className="row">
              <Select
                name="status"
                id="Status"
                className="select col-lg-4"
                label="Status"
                type="select"
                options={[
                  ["actif", "Actif"],
                  ["inactif", "Inactif"],
                ]}
                value={expert.status}
                onChange={handleChange}
              />
              <FieldInput
                name="password"
                id="password"
                className="fieldInput col-lg-6"
                label="Votre mot de passe"
                type="password"
                value={expert.password}
                onChange={handleChange}
                error={errors.password}
              />
            </div>
            <div>
              <TexteArea
                name="presentation"
                id="presentation"
                className="textArea"
                label="Présentation"
                value={expert.presentation}
                onChange={handleChange}
                error={errors.presentation}
              />
            </div>
          </div>
          <input
            className="btn btn-secondary btn-lg"
            type="submit"
            value="Envoyer"
          />
        </form>
    </>
  );
};

export default ExpertCreation;
